import * as types from "../../actionTypes";
import SesionApi from "../../../api/SesionApi";

let actionsFunction = {

  //CRUD METHODS

  // Create sesion
  createSesion: function(sesion) {
    return function(dispatch) {
      return SesionApi
        .createSesion(sesion)
        .then(sesion => {
          dispatch(actionsFunction.createSesionSuccess(sesion));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createSesionSuccess: function(sesion) {
    return { type: types.CREATE_SESION_SUCCESS, payload: sesion };
  },


  // Get sesion
  loadSesion: function(id) {
    return function(dispatch) {
      return SesionApi
        .getOneSesion(id)
        .then(sesion => {
          dispatch(actionsFunction.loadSesionSuccess(sesion));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadSesionSuccess: function(sesion) {
    return { type: types.GET_SESION_SUCCESS, payload: sesion };
  },

	
  // Save sesion
  saveSesion: function(sesion) {
    return function(dispatch) {
      return SesionApi
        .saveSesion(sesion)
        .then(sesion => {
          dispatch(actionsFunction.saveSesionSuccess(sesion));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveSesionSuccess: function(sesion) {
    return { type: types.UPDATE_SESION_SUCCESS, payload: sesion };
  },


};

export default actionsFunction;
